 Get-ADUser -Properties SamAccountName |Where-Object { $_.givenname -eq $UserName.Split('\')[1]} | select Name

 $tenantcheck=Get-ADGroupMember 'CollectionAdmins' -Server $UserName.Split('\')[0] |Where-Object { $_.objectClass -eq 'user' } |
